});

database.counter.stats(3, 2016, function(err, response) {
	/*
		[ { id: '16090308350001hme1', count: 69 },
		  { id: '16090420320001hwk1', count: 62 },
		  { id: '16083015260001nwd1', count: 53 } ]
	 */
});

database.counter.stats(3, function(err, response) {
